#edit:local smtpserv [:resolve "smtp.gmail.com"];:local Eaccount "your_account@gmail.com";:local pass "your_gmail_password";#dir must be created manually:local dir "/backups/":local sysname ([/system identity get name]);:local sysver [/system package get system version];:if (:len $dir = 0) do={ :local dir "" }:local backupfile ($dir . "$sysname-backup-" . \	[:pick [/system clock get date] 7 11] . \	[:pick [/system clock get date] 0 3] . \	[:pick [/system clock get date] 4 6] ."-". \ 	[:pick [/system clock get time] 0 2] . \ 	[:pick [/system clock get time] 3 5] . \ 		".backup");:put $backupfile/system backup save name=$backupfile:delay 3/tool e-mail send from="<$Eaccount>" to=$Eaccount server=$smtpserv \	port=587 user=$Eaccount password=$pass start-tls=yes file=$backupfile\	subject=("$sysname Full Backup (" . [/system clock get date] . ")")\	body=("$sysname full Backup file see in attachment.\nRouterOS version: \	$sysver\nTime and Date stamp: " . [/system clock get time] . " " . \	[/system clock get date]);:log info "Backups emailed successfully.";