:local sysname ([/system identity get name]):local backupfile ("$sysname-backup-" . \	[:pick [/system clock get date] 7 11] . \	[:pick [/system clock get date] 0 3] . \	[:pick [/system clock get date] 4 6] ."-". \ 	[:pick [/system clock get time] 0 2] . \ 	[:pick [/system clock get time] 3 5] . \ 		".backup");:put $backupfile/system backup save name=$backupfile:delay 60/tool e-mail send from=email@mikrotiku.cz to=váš@email.cz server=SMTP_adresa subject=("Mikrotik " . sysname . " Zaloha") body=("Zaloha ze dne " . date . " " . time) file=nazev_zaloha.backup